import React from 'react';

const Experience = () => {
  const experiences = [
    {
      title: 'Full Stack Developer',
      company: 'Freelance',
      period: '2023 - Present',
      description: 'Developing responsive web applications using modern technologies like React, Node.js, and Flutter. Specializing in creating user-centric solutions for various clients.',
      skills: ['React', 'Node.js', 'Flutter', 'MongoDB', 'Firebase']
    },
    {
      title: 'Web Development Intern',
      company: 'Tech Solutions',
      period: '2022 - 2023',
      description: 'Worked on frontend development projects, implementing responsive designs and interactive features. Collaborated with senior developers on large-scale applications.',
      skills: ['HTML', 'CSS', 'JavaScript', 'Bootstrap', 'Git']
    },
    {
      title: 'Mobile App Developer',
      company: 'Startup Project',
      period: '2023',
      description: 'Developed AQUAFLOW mobile application for water supply management system. Implemented cross-platform functionality using Flutter framework.',
      skills: ['Flutter', 'Dart', 'Firebase', 'REST APIs', 'UI/UX Design']
    }
  ];

  return (
    <section id="experience" className="card">
      <h2>Experience</h2>
      <div style={{ marginTop: '30px' }}>
        {experiences.map((exp, index) => (
          <div 
            key={index} 
            style={{
              background: '#23272a',
              borderRadius: '14px',
              padding: '28px',
              marginBottom: '24px',
              borderLeft: '4px solid var(--primary)',
              transition: 'transform 0.3s, box-shadow 0.3s'
            }}
            onMouseEnter={(e) => {
              e.target.style.transform = 'translateY(-5px)';
              e.target.style.boxShadow = '0 8px 25px rgba(0,0,0,0.2)';
            }}
            onMouseLeave={(e) => {
              e.target.style.transform = 'translateY(0)';
              e.target.style.boxShadow = 'none';
            }}
          >
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', marginBottom: '15px' }}>
              <div>
                <h3 style={{ color: 'var(--primary)', fontSize: '1.3rem', marginBottom: '5px' }}>
                  {exp.title}
                </h3>
                <p style={{ color: 'var(--accent)', fontSize: '1.1rem', fontWeight: '600' }}>
                  {exp.company}
                </p>
              </div>
              <span style={{ 
                background: 'var(--primary)', 
                color: '#fff', 
                padding: '6px 12px', 
                borderRadius: '20px', 
                fontSize: '0.9rem',
                fontWeight: '500'
              }}>
                {exp.period}
              </span>
            </div>
            <p style={{ color: 'var(--text-muted)', lineHeight: '1.6', marginBottom: '15px' }}>
              {exp.description}
            </p>
            <div style={{ display: 'flex', flexWrap: 'wrap', gap: '8px' }}>
              {exp.skills.map((skill, skillIndex) => (
                <span 
                  key={skillIndex}
                  style={{
                    background: 'rgba(0, 188, 212, 0.1)',
                    color: 'var(--primary)',
                    padding: '4px 12px',
                    borderRadius: '15px',
                    fontSize: '0.85rem',
                    border: '1px solid rgba(0, 188, 212, 0.3)'
                  }}
                >
                  {skill}
                </span>
              ))}
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Experience;
