import React, { useState, useEffect } from 'react';
import sibinImage from '../assets/sibin.jpg';

const Hero = () => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    setIsVisible(true);
  }, []);

  return (
    <section id="hero" className="hero-section">
      <div className="hero-left" style={{
        opacity: isVisible ? 1 : 0,
        transform: isVisible ? 'translateX(0)' : 'translateX(-50px)',
        transition: 'opacity 0.8s ease, transform 0.8s ease'
      }}>
        <p className="intro">Hi I am</p>
        <h1 className="name">Sibin K S</h1>
        <h2 className="role">Full Stack Developer</h2>
        <p style={{ 
          color: 'var(--text-muted)', 
          fontSize: '1.1rem', 
          marginBottom: '24px',
          lineHeight: '1.6'
        }}>
          Passionate about creating innovative digital solutions and turning ideas into reality through code.
        </p>
        <div className="hero-buttons">
          <a href="#contact" className="btn hire-me">Hire Me</a>
          <a href="/SIBIN.pdf" className="btn download-cv" download>Download CV</a>
        </div>
        <div style={{ 
          marginTop: '30px', 
          display: 'flex', 
          gap: '20px',
          flexWrap: 'wrap'
        }}>
          <div style={{ textAlign: 'center' }}>
            <div style={{ 
              fontSize: '2rem', 
              fontWeight: 'bold', 
              color: 'var(--primary)' 
            }}>2+</div>
            <div style={{ color: 'var(--text-muted)' }}>Years Experience</div>
          </div>
          <div style={{ textAlign: 'center' }}>
            <div style={{ 
              fontSize: '2rem', 
              fontWeight: 'bold', 
              color: 'var(--primary)' 
            }}>10+</div>
            <div style={{ color: 'var(--text-muted)' }}>Projects Completed</div>
          </div>
          <div style={{ textAlign: 'center' }}>
            <div style={{ 
              fontSize: '2rem', 
              fontWeight: 'bold', 
              color: 'var(--primary)' 
            }}>5+</div>
            <div style={{ color: 'var(--text-muted)' }}>Technologies</div>
          </div>
        </div>
      </div>
      <img 
        src={sibinImage} 
        alt="Sibin" 
        className="hero-image"
        style={{
          opacity: isVisible ? 1 : 0,
          transform: isVisible ? 'translateX(0)' : 'translateX(50px)',
          transition: 'opacity 0.8s ease 0.2s, transform 0.8s ease 0.2s'
        }}
      />
    </section>
  );
};

export default Hero;
