import React, { useState } from 'react';
import ccsaImg from '../assets/ccsa.jpeg';

const Certificate = () => {
  const [showDetails, setShowDetails] = useState(false);
  const [showModal, setShowModal] = useState(false);

  const handleMouseMove = () => {
    setShowDetails(true);
  };

  const handleMouseLeave = () => {
    setShowDetails(false);
  };

  const handleClick = () => {
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
  };

  return (
    <section id="certificates" className="card">
      <h2>Certificate</h2>
      <div className="certificate-container">
        <div 
          className={`certificate-details ${showDetails ? 'show' : ''}`}
          style={{ display: showDetails ? 'block' : 'none' }}
        >
          <h3 style={{ color: 'var(--primary)', marginBottom: '8px' }}>CCSA Certificate</h3>
          <p style={{ color: '#b0b0b0', fontSize: '1.08rem' }}>
            Certified Cyber Security Associate (CCSA)<br />
            Issued by: [Your Issuer Name Here]<br />
            Date: [Issue Date Here]
          </p>
          <p style={{ marginTop: '10px', color: '#eee' }}>
            Successfully completed the CCSA program, demonstrating knowledge and skills in cyber security fundamentals, network defense, and threat mitigation.
          </p>
        </div>
        <img 
          src={ccsaImg} 
          alt="CCSA Certificate" 
          className="certificate-pdf"
          style={{ width: 520, height: 370, borderRadius: 10, objectFit: 'cover', cursor: 'pointer', boxShadow: '0 2px 8px rgba(0,0,0,0.13)' }}
          onMouseMove={handleMouseMove}
          onMouseLeave={handleMouseLeave}
          onClick={handleClick}
        />
      </div>

      {/* Modal */}
      <div 
        className={`certificate-modal ${showModal ? 'active' : ''}`}
        onClick={closeModal}
      >
        <div className="modal-content" onClick={(e) => e.stopPropagation()}>
          <span className="close-modal" onClick={closeModal}>&times;</span>
          <img 
            src={ccsaImg} 
            alt="CCSA Certificate Full" 
            className="modal-certificate-img"
            style={{ width: 900, height: 600, borderRadius: 10, objectFit: 'cover', background: '#23272a' }}
          />
          <div className="modal-certificate-details">
            <h3 style={{ color: 'var(--primary)', marginBottom: '8px' }}>CCSA Certificate</h3>
            <p style={{ color: '#b0b0b0', fontSize: '1.08rem' }}>
              Certified Cyber Security Associate (CCSA)<br />
              Issued by: [Your Issuer Name Here]<br />
              Date: [Issue Date Here]
            </p>
            <p style={{ marginTop: '10px', color: '#eee' }}>
              Successfully completed the CCSA program, demonstrating knowledge and skills in cyber security fundamentals, network defense, and threat mitigation.
            </p>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Certificate;
