import React, { useState, useEffect } from 'react';

const testimonials = [
  {
    name: 'Akhil P.',
    role: 'Project Manager',
    text: 'Sibin is a highly skilled developer with a great eye for detail. His work on our web app was outstanding and delivered ahead of schedule!'
  },
  {
    name: 'Divya S.',
    role: 'UI/UX Designer',
    text: 'Working with Sibin was a pleasure. He transformed our designs into a beautiful, responsive website with smooth animations.'
  },
  {
    name: 'Rahul K.',
    role: 'Startup Founder',
    text: 'Sibin built our MVP in record time and was always available for feedback and improvements. Highly recommended!'
  }
];

const Testimonials = () => {
  const [index, setIndex] = useState(0);

  useEffect(() => {
    const timer = setTimeout(() => {
      setIndex((prev) => (prev + 1) % testimonials.length);
    }, 4000);
    return () => clearTimeout(timer);
  }, [index]);

  return (
    <section id="testimonials" className="card" style={{ minHeight: 200, textAlign: 'center', position: 'relative', overflow: 'hidden' }}>
      <h2>Testimonials</h2>
      <div style={{ marginTop: 30, minHeight: 120 }}>
        {testimonials.map((t, i) => (
          <div
            key={i}
            className="testimonial-slide"
            style={{
              opacity: i === index ? 1 : 0,
              transform: i === index ? 'scale(1)' : 'scale(0.95)',
              transition: 'opacity 0.7s, transform 0.7s',
              position: i === index ? 'relative' : 'absolute',
              left: 0, right: 0, top: 0
            }}
          >
            <p style={{ fontSize: '1.15rem', color: 'var(--text-main)', fontStyle: 'italic', marginBottom: 18 }}>
              "{t.text}"
            </p>
            <div style={{ color: 'var(--primary)', fontWeight: 600 }}>{t.name}</div>
            <div style={{ color: 'var(--text-muted)', fontSize: '0.98rem' }}>{t.role}</div>
          </div>
        ))}
      </div>
      <div style={{ marginTop: 18 }}>
        {testimonials.map((_, i) => (
          <span
            key={i}
            style={{
              display: 'inline-block',
              width: 10,
              height: 10,
              borderRadius: '50%',
              background: i === index ? 'var(--primary)' : '#444',
              margin: '0 5px',
              transition: 'background 0.3s'
            }}
          ></span>
        ))}
      </div>
    </section>
  );
};

export default Testimonials;
